import * as React from 'react';
import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";
import { Grid } from '@material-ui/core';

// tslint:disable-next-line
const { Parallax, Background } = require('react-parallax');

const useStyles = makeStyles((theme: Theme) => 
  createStyles({
    root: {
      display: 'block',
    },
    deepestLayer: {
      position: 'relative',
      zIndex: 1
    },
    backgroundImage: {
      position: 'relative',
      top: '200px',
      width: `${window.innerWidth}px`
    },
    middleLayer: {
      position: 'relative',
      top: 0,
      left: 0,
      zIndex: 2,
      width: '100%',
    },
  })
)

interface Props {
  backgroundImage: string;
}

const SharedLayout: React.FC<Props> = ({ backgroundImage }) => {
  const classes = useStyles();

  return (
    <Grid className={classes.root}>
      {/* Middle Layer - Parallax */}
      <Grid className={classes.middleLayer}>
        <Parallax strength={500}>
          <Background>
            <img src={backgroundImage} className={classes.backgroundImage} />
          </Background>
          <div style={{height: '500px'}}></div>
        </Parallax>
      </Grid>
    </Grid>
  )
}

export default SharedLayout;
