import React from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import { Grid, Box, Typography, Paper } from '@material-ui/core/';
import BackgroundPicture from 'assets/images/About-Us-Cover-Page-v2.jpg';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    parent: {
      position: 'relative'
    },
    aspectRatio: {
      width: '100%',
      height: 'auto'
    },
    content: {
      position: 'absolute',
      width: '100%',
      height: '100%',
      top: 0,
      left: 0
    },
    aboutUs: {
      width: '100%',
      height: '100%',
    },
    whiteText: {
      color: 'white',
      font: 'Arial Arial Tahoma sans-serif',
      fontWeight: 'bolder'
    }
  })
);

const AboutPage: React.FC = () => {
  const classes = useStyles();

  return (
    <div>
      <div className={classes.parent}>
        <img className={classes.aspectRatio} src={BackgroundPicture} />
        <div className={classes.content}>
          <Grid container className={classes.aboutUs} justify='center' alignContent='center'>
            <Typography variant='h2' className={classes.whiteText}>About Us</Typography>
          </Grid>
        </div>
      </div>
    </div>
  );
};

export default AboutPage;
